import React, {useState, useEffect} from "react"

function ListPlantItems(){
    const [list, setList]=useState([])
    useEffect(()=> {
        fetch("http://localhost:3000/plants")
        .then(res=>res.json())
        .then((data)=>setList(data))
    }, [])
    return (
        <div>
            {list.map(plant=>{
                < PlantItem plant={plant}/>
            })}

        </div>
    )
}
 export default ListPlantItems;
